import '../styles/UserPerformance.scss';
import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis } from 'recharts';

export const UserPerformance = ({ userPerformance }) => {
  return (
    <div className='UserPerformanceContainer'>
      <RadarChart
        cx={130} // center x
        cy={130} // center y
        outerRadius={100} // half of the chart's width or height
        width={260} // width of the chart
        height={260} // height of the chart
        data={userPerformance.data.map(item => ({
          kind: userPerformance.kind[item.kind],
          value: item.value,
        }))}
      >
        <PolarGrid radialLines={false} stroke="white" />
        <PolarAngleAxis dataKey="kind" axisLine={false} stroke="white" tickLine={false} />
        <Radar
          name="Performance"
          dataKey="value"
          stroke="none"
          fill="#FF0101"
          fillOpacity={0.7}
        />
      </RadarChart>
    </div>
  );
};
