import '../styles/Home.scss';
import { Welcome } from './Welcome';
import { useParams } from 'react-router-dom';
import { getUserFirstNameById } from '../services/dataService.js';


export function Home() {
  const { id } = useParams();
    // Converting userId to a number before calling the function
    const userName = getUserFirstNameById(Number(id));
  return (
    <section>
      <Welcome userId={userName}/>
    </section>
  );
}
