import {
  USER_MAIN_DATA,
  USER_ACTIVITY,
  USER_AVERAGE_SESSIONS,
  USER_PERFORMANCE,
} from '../file/data.js'

import {  
        UserMainData, 
        UserActivity, 
        UserAverageSessions, 
        UserPerformance 
} from '../services/dataModel';

