import '../styles/LengthReport.scss';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const LengthReport = ({ lengthActivity }) => {
  // If there's no data for the given user ID, return null
  if (!lengthActivity) return null;

  const dayLabels = ["L", "M", "M", "J", "V", "S", "D"];

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          height: '70px',
          width: '40px',
          backgroundColor: 'red',
          padding: '10px',
          color: 'white',
          fontSize: '10px',
          fontWeight: 'bold',
          textAlign: 'center',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
        }}>
          <p style={{ margin: 0, marginBottom: '20px' }}>{payload[0].payload.sessionLength} minutes</p>
        </div>
      );
    }

    return null;
  };


  return (
    <div className='backgroundcolor'>
      <LineChart
        width={500}
        height={300}
        data={lengthActivity}
        margin={{
          top: 0, right: 0, left: 0, bottom: 0,
        }}
      >
        <defs>
          <linearGradient id="colorUv" x1="0" y1="0" x2="1" y2="0">
            <stop offset="5%" stopColor="white" stopOpacity={0.3}/>
            <stop offset="95%" stopColor="white" stopOpacity={1}/>
          </linearGradient>
        </defs>
        <CartesianGrid strokeDasharray="3 3" vertical={false} horizontal={false} />
        <XAxis 
          dataKey="day" 
          tickFormatter={(tickItem) => dayLabels[tickItem - 1]}
          tickLine={false}
          axisLine={false}
        />
        <YAxis 
          tick={false}
          tickLine={false}
          axisLine={false}
        />
        <Tooltip content={<CustomTooltip />} />
        <Line 
          type="monotone" 
          dataKey="sessionLength" 
          stroke="url(#colorUv)" 
          activeDot={{ r: 8 }} 
          strokeWidth={4}
        />
      </LineChart>
    </div>
  );
}
