import React, { useState, useEffect } from 'react';
import {
  getUserMainDataById,
  getUserActivityById,
  getUserAverageSessionsById,
  getUserPerformanceById,
} from '../services/dataService'; // please replace with your actual path

const DisplayRawData = ({ userId }) => {
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const mainData = await getUserMainDataById(userId);
        const activityData = await getUserActivityById(userId);
        const averageSessionsData = await getUserAverageSessionsById(userId);
        const performanceData = await getUserPerformanceById(userId);

        setData({
          mainData,
          activityData,
          averageSessionsData,
          performanceData,
        });
      } catch (e) {
        setError('An error occurred: ' + e.toString());
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
};

export default DisplayRawData;
