import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis } from 'recharts';

export const UserPerformance = ({ userPerformance }) => {
  return (
    <div>
      {userPerformance.map((user, index) => (
        <div key={index}>
          <h3>User ID: {user.userId}</h3>
          <RadarChart
            cx={300}
            cy={250}
            outerRadius={150}
            width={600}
            height={500}
            data={user.data.map(item => ({
              kind: user.kind[item.kind],
              value: item.value,
            }))}
          >
            <PolarGrid />
            <PolarAngleAxis dataKey="kind" />
            <PolarRadiusAxis />
            <Radar
              name="Performance"
              dataKey="value"
              stroke="#8884d8"
              fill="#8884d8"
              fillOpacity={0.6}
            />
          </RadarChart>
        </div>
      ))}
    </div>
  );
}
