import '../styles/LengthReport.scss';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const LengthReport = ({ lengthActivity }) => {
  // If there's no data for the given user ID, return null
  if (!lengthActivity) return null;

  return (
    <LineChart
      width={500} // Set the width of the chart
      height={300} // Set the height of the chart
      data={lengthActivity} // Pass the data to the chart
      margin={{
        top: 5, right: 30, left: 20, bottom: 5,
      }}
    >
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="name" /> {/* Assume each data point has a 'name' key for the X-axis */}
      <YAxis />
      <Tooltip />
      <Line type="monotone" dataKey="uv" stroke="#8884d8" activeDot={{ r: 8 }} /> {/* Assume 'uv' is the key for the Y-axis data */}
    </LineChart>
  );
}
