import React from 'react';
import { RadialBarChart, RadialBar, ResponsiveContainer } from 'recharts';

export const UserScoreGauge = ({ userScore }) => {
  // Converting score to percentage and ensuring it's in [0, 100]
  const scorePercentage = Math.min(Math.max(userScore * 100, 0), 100);

  return (
    <ResponsiveContainer width="100%" height={200}>
      <RadialBarChart
        innerRadius="70%"
        outerRadius="100%"
        data={[{ name: 'Score', uv: scorePercentage, fill: '#FF0101' }]}
        startAngle={180}
        endAngle={0}
      >
        <RadialBar
          minAngle={15}
          label={{ position: 'insideStart', fill: '#fff' }}
          background
          clockWise
          dataKey="uv"
        />
      </RadialBarChart>
    </ResponsiveContainer>
  );
};
