import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

export const UserScorePie = ({ userScore }) => {
  // Ensuring userScore is in [0, 1]
  const score = Math.min(Math.max(userScore, 0), 1);
  const data = [
    { name: 'Score', value: score },
    { name: 'Remaining', value: 1 - score },
  ];

  return (
    <ResponsiveContainer width="100%" height={200}>
      <PieChart>
        <Pie
          data={data}
          cx="50%"
          cy="50%"
          innerRadius="60%"
          outerRadius="80%"
          fill="#FF0101"
          dataKey="value"
          startAngle={90}
          endAngle={-270}
        >
          <Cell key={`cell-0`} fill="#FF0101" /> {/* Score fill color */}
          <Cell key={`cell-1`} fill="#D3D3D3" /> {/* Remaining fill color */}
        </Pie>
      </PieChart>
    </ResponsiveContainer>
  );
};
