import React from 'react';
import '../styles/Score.scss'
import {
  PieChart, Pie, Sector, Cell,
  RadialBarChart,
  RadialBar,
  PolarAngleAxis,
  ResponsiveContainer,
} from 'recharts';

export const Score = ({ userScore }) => {
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];
  // Ensuring userScore is in [0, 1]
  const score = Math.min(Math.max(userScore, 0), 1) * 100;
  const data = [{ name: 'Score', value: score, fill: '#FF0101' }];

  return (
    <div className='scoreContainer'>
    <div className='titreScore'>Score</div>
    <ResponsiveContainer 
      width={260} 
      height={260} 
      style={{ backgroundColor: '#FBFBFB' }}
    >
<PieChart width={800} height={400} onMouseEnter={this.onPieEnter}>
        <Pie
          data={data}
          cx={120}
          cy={200}
          innerRadius={60}
          outerRadius={80}
          fill="#8884d8"
          paddingAngle={5}
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Pie
          data={data}
          cx={420}
          cy={200}
          startAngle={180}
          endAngle={0}
          innerRadius={60}
          outerRadius={80}
          fill="#8884d8"
          paddingAngle={5}
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
      </PieChart>
    </ResponsiveContainer>
    <div className='circleCenter'>
      <div className='scoreText'> <span className='percentage'>{score}%</span> de votre objectif</div>
      </div>
    </div>
  );
};
