import '../styles/Home.scss';
import { useParams } from 'react-router-dom';
import { getUserFirstNameById } from '../services/dataService.js';
import { getUserAverageSessionsById } from '../services/dataService.js';
import { getUserActivityById } from '../services/dataService.js';
import { getUserPerformanceById } from '../services/dataService.js';
import { getUserMainDataById } from '../services/dataService.js';
import { Welcome } from './Welcome';
import { ActivityReport } from './ActivityReport';
import { LengthReport } from './LengthReport';
import { UserPerformance } from './UserPerformance';
import { Score } from './Score'
import { Card } from './Card'

export function Home() {
  const { id } = useParams();
  const userId = Number(id);

  // Getting user data
  const userName = getUserFirstNameById(userId);
  const userActivity = getUserActivityById(userId);
  const lengthActivity = getUserAverageSessionsById(userId);
  const userPerformance = getUserPerformanceById(userId);
  const userData = getUserMainDataById(userId);
  const userScore = userData?.todayScore ?? userData?.score ?? 0;

  // Extracting nutritional data from userData
  const { proteinCount, carbohydrateCount, lipidCount } = userData?.keyData || {};

  return (
    <section className='home'>
      <Welcome userName={userName}/>
      <div className='leftColumn'>
        <div className='upperRow'>
          <ActivityReport userActivity={userActivity} />
        </div>
        <div className='bottomRow'>
          <LengthReport lengthActivity={lengthActivity} />
          <UserPerformance userPerformance={userPerformance} />
          <Score userScore={userScore} />
        </div>
      </div>
      <div className='rightColumn'>
          <Card title="Fat" value={lipidCount} unit="g" imageUrl='../img/fat-icon.svg' />
          <Card title="Carbs" value={carbohydrateCount} unit="g" imageUrl='../img/carbs-icon.svg' />
          <Card title="Protein" value={proteinCount} unit="g" imageUrl='../img/protein-icon.svg' />
      </div>
    </section>
  );
}
