// ActivityReport.js
import '../styles/ActivityReport.scss';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const ActivityReport = ({ userActivity }) => {
  // Assume userActivity is already the correct activity data for a specific user
  // Mapping over the sessions array to replace the day with its index+1
  const data = userActivity.sessions.map((session, index) => ({
    ...session,
    day: index + 1
  }));

  // Calculating the minimum and maximum kilogram values
  const kilogramValues = data.map(session => session.kilogram);
  const minKilogram = Math.min(...kilogramValues) - 1;
  const maxKilogram = Math.max(...kilogramValues) + 1;

  // Setting a maxY for calories
  const maxCalories = 400;

  // Create a custom tick array with only three values: minKilogram, the average, and maxKilogram
  const yAxisTicks = [minKilogram, (minKilogram + maxKilogram) / 2, maxKilogram];

  return (
    <section className='UserActivity'>
      <div className='legende'>
        <text className='title'>Activité quotidienne</text>
        <div>
          <text className=''>Poids(kg) Calories brûlées(kCal)</text>
        </div>
      </div>
      <BarChart
        width={835}
        height={320}
        data={data}
        margin={{
          top: 5, right: 0, left: 0, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" vertical={false} />
        <XAxis dataKey="day" tickLine={false} />
        <YAxis 
          yAxisId="left" 
          orientation="right" 
          domain={[minKilogram, maxKilogram]} 
          ticks={yAxisTicks}  // Pass the custom tick array to the ticks prop
        />
        <YAxis 
          yAxisId="right" 
          orientation="right" 
          domain={[0, maxCalories]} 
          hide={true} 
        />
        <Tooltip />
        <Bar yAxisId="left" radius={[20, 20, 0, 0]} maxBarSize={10} dataKey="kilogram" fill="#24282c" />
        <Bar yAxisId="right" radius={[20, 20, 0, 0]} maxBarSize={10} dataKey="calories" fill="#e60001" />
      </BarChart>
    </section>
  );
}
