import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis } from 'recharts';

export const UserPerformance = ({ userPerformance }) => {
  return (
    <div style={{ backgroundColor: '#282D30', padding: '20px', color: 'white' }}>
      <h3>User ID: {userPerformance.userId}</h3>
      <RadarChart
        cx={300}
        cy={250}
        outerRadius={150}
        width={600}
        height={500}
        data={userPerformance.data.map(item => ({
          kind: userPerformance.kind[item.kind],
          value: item.value,
        }))}
      >
        <PolarGrid radialLines={false} stroke="white" />
        <PolarAngleAxis dataKey="kind" axisLine={false} stroke="white" />
        <Radar
        tickLine={false}
          name="Performance"
          dataKey="value"
          stroke="white"
          strokeWidth={1}
          fill="#FF0101"
          fillOpacity={0.7}
        />
      </RadarChart>
    </div>
  );
};
