import '../styles/LengthReport.scss';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const LengthReport = ({ lengthActivity }) => {
    console.log(lengthActivity)
  // If there's no data for the given user ID, return null
  if (!lengthActivity) return null;

  const dayLabels = ["L", "M", "M", "J", "V", "S", "D"];

  return (
    <div className='backgroundcolor'>
        <LineChart
        width={500}
        height={300}
        data={lengthActivity}
        margin={{
            top: 0, right: 0, left: 0, bottom: 0,
        }}
        >
                  <defs>
        <linearGradient id="colorUv" x1="0" y1="0" x2="0" y2="1">
          <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
          <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
        </linearGradient>
      </defs>
        <CartesianGrid strokeDasharray="3 3" vertical={false} horizontal={false} />
        <XAxis 
            dataKey="day" 
            tickFormatter={(tickItem) => dayLabels[tickItem - 1]}
            tickLine={false}
            axisLine={false}
        />
        <YAxis 
            tick={false}
            tickLine={false}
            axisLine={false}
        />
        <Tooltip />
        <Line type="monotone" dataKey="sessionLength" stroke="#8884d8" activeDot={{ r: 8 }} />
        </LineChart>
    </div>
  );
}
