// ActivityReport.js
import '../styles/ActivityReport.scss';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const ActivityReport = ({ userActivity }) => {
  // Assume userActivity is already the correct activity data for a specific user
  const data = userActivity.sessions;

  return (
    <section className='UserActivity'>
      <BarChart
        width={835}
        height={320}
        data={data}
        margin={{
          top: 5, right: 5, left: 5, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="day" />
        <YAxis />
        <Tooltip />
        <Bar radius={[20, 20, 0, 0]} maxBarSize={10} dataKey="kilogram" fill="#24282c" />
        <Bar radius={[20, 20, 0, 0]} maxBarSize={10} dataKey="calories" fill="#e60001" />
      </BarChart>
    </section>
  );
}
