import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, ResponsiveContainer } from 'recharts';
import '../styles/UserPerformance.scss';

// Translation object
const translations = {
  cardio: 'cardio',
  energy: 'énergie',
  endurance: 'endurance',
  strength: 'force',
  speed: 'vitesse',
  intensity: 'intensité'
};

export const UserPerformance = ({ userPerformance }) => {
  return (
    <div className='UserPerformanceContainer'>
        <ResponsiveContainer width="100%" height={260}>
            <RadarChart
                cx="50%"
                cy="50%"
                outerRadius="40%"
                data={userPerformance.data.map(item => ({
                    kind: translations[userPerformance.kind[item.kind]],
                    value: item.value,
                }))}
                ></RadarChart>
            <RadarChart
                cx={130}
                cy={130}
                outerRadius={100}
                width={260}
                height={260}
                data={userPerformance.data.map(item => ({
                kind: translations[userPerformance.kind[item.kind]], // using the translation object here
                value: item.value,
                }))}
            >
                <PolarGrid radialLines={false} stroke="white" />
                <PolarAngleAxis
                dataKey="kind"
                axisLine={false}
                tickLine={false}
                tick={{
                    fill: 'white', // text color
                    fontSize: '12px' // font size
                }}
                />
                <Radar
                name="Performance"
                dataKey="value"
                stroke="none"
                fill="#FF0101"
                fillOpacity={0.7}
                />
            </RadarChart>
      </ResponsiveContainer>
    </div>
  );
};
