import '../styles/LengthReport.scss';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

const dayLabels = ["L", "M", "M", "J", "V", "S", "D"];

export const LengthReport = ({ lengthActivity }) => {
  // If there's no data for the given user ID, return null
  if (!lengthActivity) return null;

  return (
    <LineChart
      width={500}
      height={300}
      data={lengthActivity.sessions} // use lengthActivity.sessions to access the session data
      margin={{
        top: 5, right: 30, left: 20, bottom: 5,
      }}
    >
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis 
        dataKey="day" 
        tickFormatter={(tickItem) => dayLabels[tickItem - 1]} // format the ticks to use day labels
      />
      <YAxis />
      <Tooltip />
      <Line type="monotone" dataKey="sessionLength" stroke="#8884d8" activeDot={{ r: 8 }} />
    </LineChart>
  );
}
