import {
    USER_MAIN_DATA,
    USER_ACTIVITY,
    USER_AVERAGE_SESSIONS,
    USER_PERFORMANCE,
  } from '../file/data.js'

import {  UserMainData, 
          UserActivity, 
          UserAverageSessions, 
          UserPerformance 
  } from './path_to/dataModels';


  //function to get the user's name
  export const getUserFirstNameById = (id) => {
    const user = USER_MAIN_DATA.find(user => user.id === id);
    return user ? user.userInfos.firstName : 'User not found';
  };

  // Function to get the main data by user ID
  export const getUserMainDataById = async (id) => {
    if (process.env.REACT_APP_USE_MOCKED_DATA === 'true') {
      // Use mocked data
      // Wrapping the mocked data retrieval in a promise
      return Promise.resolve(USER_MAIN_DATA.find(user => user.id === id));
    } else {
      // Fetch data from API
      try {
        const response = await fetch(`https://api.example.com/user/${id}`);
        if (!response.ok) {
          throw new Error('Network response was not ok' + response.statusText);
        }
        const data = await response.json();
        return data;
      } catch (error) {
        console.error('Fetch error: ', error);
        throw error;
      }
    }
  };


  // Function to get the activity data by user ID
  export const getUserActivityById = (userId) => {
    return USER_ACTIVITY.find(activity => activity.userId === userId);
  };

  // Function to get the average session data by user ID
export const getUserAverageSessionsById = (userId) => {
  const userAverageSession = USER_AVERAGE_SESSIONS.find(session => session.userId === userId);
  return userAverageSession ? userAverageSession.sessions : null;
};


  // Function to get the performance data by user ID
  export const getUserPerformanceById = (userId) => {
    return USER_PERFORMANCE.find(performance => performance.userId === userId);
  };

