import '../styles/Welcome.scss';
import React, { useState, useEffect } from 'react';
import { getUserFirstNameById } from '../services/dataService.js'

export function Welcome({ userId }) {
  const [firstName, setFirstName] = useState('');

  useEffect(() => {
    // Load the user's first name when the component mounts or userId changes
    async function loadFirstName() {
      const name = await getUserFirstNameById(userId);
      setFirstName(name);
