import '../styles/Welcome.scss';
import React, { useState, useEffect } from 'react';
import { getUserFirstNameById } from '../services/dataService.js'

export const Welcome = ({ userId }) => {
  const [userName, setUserName] = useState('');

  useEffect(() => {
    // Define an async function
    const fetchUserName = async () => {
      const name = await getUserFirstNameById(userId);
      setUserName(name); // Set the username in the state after it is fetched
    };

    // Call the async function
    fetchUserName();
  }, [userId]); // This effect depends on the userId prop

