// ActivityReport.js
import '../styles/ActivityReport.scss';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';
import React from 'react';

export const ActivityReport = ({ userActivity }) => {
  // Assume userActivity is already the correct activity data for a specific user
  const data = userActivity.sessions;

  return (
    <section className='UserActivity'>
      <LineChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="day" />
        <YAxis />
        <Tooltip />
        <Line type="monotone" dataKey="kilogram" stroke="#8884d8" activeDot={{ r: 8 }} />
        <Line type="monotone" dataKey="calories" stroke="#82ca9d" />
      </LineChart>
    </section>
  );
}
