import React from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis } from 'recharts';
import '../styles/UserPerformance.scss';

// Translation object
const translations = {
  cardio: 'cardio',
  energy: 'énergie',
  endurance: 'endurance',
  strength: 'force',
  speed: 'vitesse',
  intensity: 'intensité'
};

export const UserPerformance = ({ userPerformance }) => {
  return (
    <div className='UserPerformanceContainer'>
      <RadarChart
        cx={130}
        cy={130}
        outerRadius={100}
        width={260}
        height={260}
        data={userPerformance.data.map(item => ({
          kind: translations[userPerformance.kind[item.kind]], // using the translation object here
          value: item.value,
        }))}
      >
        <PolarGrid radialLines={false} stroke="white" />
        <PolarAngleAxis
          dataKey="kind"
          axisLine={false}
          tickLine={false}
          tick={{
            fill: 'white', // text color
            fontSize: '12px' // font size
          }}
        />
        <Radar
          name="Performance"
          dataKey="value"
          stroke="none"
          fill="#FF0101"
          fillOpacity={0.7}
        />
      </RadarChart>
    </div>
  );
};
