import React from 'react';
import '../styles/Score.scss'
import {
  RadialBarChart,
  RadialBar,
  PolarAngleAxis,
  ResponsiveContainer,
} from 'recharts';

export const Score = ({ userScore }) => {
  // Ensuring userScore is in [0, 1]
  const score = Math.min(Math.max(userScore, 0), 1) * 100;
  const data = [{ name: 'Score', value: score, fill: '#FF0101' }];

  return (

      <ResponsiveContainer width="100%" height={200}>
        <RadialBarChart
          innerRadius="60%"
          outerRadius="80%"
          data={data}
          startAngle={90}
          endAngle={-270}
        >
          <PolarAngleAxis type="number" domain={[0, 100]} angleAxisId={0} tick={false} />
          <RadialBar
            background
            clockWise
            dataKey="value"
            cornerRadius={10} // You can adjust the corner radius here
            fill="#FF0101"
          />
        </RadialBarChart>
      </ResponsiveContainer>

  );
};
