import React from 'react';
import '../styles/Score.scss'
import {
  RadialBarChart,
  RadialBar,
  PolarAngleAxis,
  ResponsiveContainer,
} from 'recharts';

export const Score = ({ userScore }) => {
  // Ensuring userScore is in [0, 1]
  const score = Math.min(Math.max(userScore, 0), 1) * 100;
  const data = [{ name: 'Score', value: score, fill: '#FF0101' }];

  return (
    <div className='scoreContainer' style={{ position: 'relative' }}>
      <ResponsiveContainer 
        width={260} 
        height={260} 
        style={{ backgroundColor: '#FBFBFB' }} // Setting the background color here
      >
        <RadialBarChart
          innerRadius="60%"
          outerRadius="72%"
          data={data}
          startAngle={180}
          endAngle={-270}
          background={<circle cx="50%" cy="50%" outerRadius="80%" />}
        >
          <PolarAngleAxis type="number" domain={[0, 100]} angleAxisId={0} tick={false} />
          <RadialBar
            clockWise
            dataKey="value"
            cornerRadius={10}
          />
        </RadialBarChart>
        {/* White circle at the center */}
        <div style={{
          backgroundColor: 'white',
          borderRadius: '50%',
          position: 'absolute',
          top: '50%',
          left: '50%',
          width: '120px', // Adjust the size as needed
          height: '120px', // Adjust the size as needed
          transform: 'translate(-50%, -50%)'
        }}/>
      </ResponsiveContainer>
    </div>
  );
};
